function regressivePromotion(){var e,o,n,s,t=(new Date(document.querySelector(".end-promotion").dataset.end.replace("-","/")).getTime()-(new Date).getTime())/1e3;if(e=parseInt(t/86400),t%=86400,o=parseInt(t/3600),t%=3600,n=parseInt(t/60),s=parseInt(t%60),isNaN(e)||isNaN(o)||isNaN(n)||isNaN(s))document.querySelector(".contador-promotion").style.display="none";else{var a=['<div class="message-promo-flex"><h6>Essa promoção termina em: </h6><div class="contador-flex"><p class="promo-item" id="dia"> <span>'+e+' dias </span> </p><p class="promo-item" id="hora"> <span>'+o+' h </span> </p><p class="promo-item" id="minuto"> <span>'+n+' min </span> </p><p class="promo-item" id="segundo"> <span>'+s+" seg </span> </p></div></div>"];document.querySelector(".contador-promotion .content").innerHTML=a.join(" ")}}document.addEventListener("DOMContentLoaded",function(e){regressivePromotion(),setInterval(regressivePromotion,1e3)});